(()=>{let e=new Date(document.getElementById("limited-countdown").innerHTML).getTime(),n=setInterval((function(){let t=(new Date).getTime(),o=e-t,r=Math.floor(o/864e5),i=Math.floor(o%864e5/36e5),l=Math.floor(o%36e5/6e4),c=Math.floor(o%6e4/1e3);document.querySelector(".banner__limited-countdown_days").innerHTML=r,document.querySelector(".banner__limited-countdown_hours").innerHTML=i,document.querySelector(".banner__limited-countdown_mins").innerHTML=l,document.querySelector(".banner__limited-countdown_sec").innerHTML=c,o<0&&(clearInterval(n),document.querySelector(".banner__limited-countdown").innerHTML="EXPIRED")}),1e3)})();